// const apiKey = 'AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA'

export const authApi = {

    signIn(email, password) {
       fetch('https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA', {
        method: 'POST',
        body: JSON.stringify({
            email: email,
            password: password,
            returnSecureToken: true
        }),
        headers: {
          'Content-Type': 'application/json'
        }
       }
       ).then( res => {
           if(res.ok) {
             return  alert('ok')
           } else {
               res.json().then( data => {
                   console.log(data);
               })
           }
       })
    //    if (!response.ok) {
    //     throw new Error('Server data is not accesssible')
    // };
    }
}
