const apiKey = 'AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA'

export const authApi = {

    signIn(email, password) {
       fetch('https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=`${apiKey}`', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: {
            'email': email,
            'password': password,
            'token': true
        }
       })
    //    if (!response.ok) {
    //     throw new Error('Server data is not accesssible')
    // };
    }
}
