 const apiKey ='AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA';
 const signRequestBody = (email, password) => {
     return ({
    method: 'POST',
    body: JSON.stringify({
        email: email,
        password: password,
        returnSecureToken: true
    }),
    headers: {
      'Content-Type': 'application/json'
    }
 })}


export const authApi = {

    signUP(email, password) {
       fetch('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA', signRequestBody(email, password))
       .then( res => {
           if(res.ok) {
             return  alert('ok')
           } else {
            return res.json().then( data => {
                let errorMessage = 'Authentication failed!'
                if(data && data.error && data.error.message) {
                    errorMessage = data.error.message
            }
            alert(errorMessage);
               });
           }
       })
    },

    signIN(email, password) {
        fetch(`https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=${apiKey}`, signRequestBody(email, password))
        .then( res => {
            if(res.ok) {
              return  alert('ok')
            } else {
              return res.json().then( data => {
                  let errorMessage = 'Authentication failed!'
                  if(data && data.error && data.error.message) {
                      errorMessage = data.error.message
              }
              alert(errorMessage);
            });
            }
        });
     }
}
