 const apiKey ='AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA';
 const signRequestBody = {
    method: 'POST',
    body: JSON.stringify({
        email: email,
        password: password,
        returnSecureToken: true
    }),
    headers: {
      'Content-Type': 'application/json'
    }
 }


export const authApi = {

    signUP(email, password) {
       fetch('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA', signRequestBody)
       .then( res => {
           if(res.ok) {
             return  alert('ok')
           } else {
             return res.json().then( data => {
                   console.log(data);
               })
           }
       })
    },

    signIN(email, password) {
        fetch(`https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=${apiKey}`, signRequestBody)
        .then( res => {
            if(res.ok) {
              return  alert('ok')
            } else {
              return res.json().then( data => {
                    console.log(data);
                })
            }
        })
     }
}
