// const apiKey = 'AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA'

export const authApi = {

    signIn(email, password) {
       fetch('https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyBrEdevpKRpF13aBB3W8B6ygzm4hA6LLeA', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            email: email,
            password: password,
            returnSecureToken: true
        })
       })
    //    if (!response.ok) {
